create database tourBase;

GRANT ALL privileges ON tourBase.* TO ohgiraffers@'%';

use tourBase;

CREATE TABLE Tour_Info
(
    TOUR_CODE INT auto_increment PRIMARY KEY,
    TOUR_TITLE	VARCHAR(512),
    ADDR	VARCHAR(512),
    TOUR_CATEGORY	VARCHAR(512),
    FACILITIES	VARCHAR(3000),
    SUPPLIES	VARCHAR(3000),
    POLICY	VARCHAR(3000),
    NOTICE	VARCHAR(3000)
);



CREATE TABLE Tour_Category_Sort
(
    TOUR_Cate_Code INT auto_increment PRIMARY KEY,
    Tour_Category_Name VARCHAR(50)
);

