INSERT INTO Tour_Info (TOUR_TITLE, ADDR, TOUR_CATEGORY, FACILITIES, SUPPLIES, POLICY, NOTICE) VALUES ('열대과일농장 유진팡', '제주특별자치도 서귀포시 원님로399번길 31-7 ', '관광지', '사우나, GS편의점, 노래방, 당구장, 주차장(무료)', '반려견 방석, 반려견 식기, 배변 패드', '중, 소형견 (10kg 이하)', '지정된 펫룸만 이용 가능합니다. / 광견병 접종이 완료된 반려견만 동반 가능합니다. (체크인 시 프론트에서 확인 必, 애견 수첩 또는 접종 확인서) / 객실 외에는 반려견 리드줄 착용 필수입니다.<br>*반려견 동반 운영 정책은 현지 사정에 따라 변동 될 수 있습니다.');
INSERT INTO Tour_Info (TOUR_TITLE, ADDR, TOUR_CATEGORY, FACILITIES, SUPPLIES, POLICY, NOTICE) VALUES ('강문해변', '강원특별자치도 강릉시 창해로 352 ', '관광지', '', '', '모든 견종', '반려견 인식표 및 목줄, 입마개 착용 권장<br>실외만 동반 가능<br>성수기 및 해수욕장 개장 시 위생관리를 위해 입장이 제한<br>반려동물과 함께 사진 찍을 수 있는 다양한 포토존 있음');
INSERT INTO Tour_Info (TOUR_TITLE, ADDR, TOUR_CATEGORY, FACILITIES, SUPPLIES, POLICY, NOTICE) VALUES ('하평해변(강릉)', '강원특별자치도 강릉시 사천면 진리해변길 155 ', '관광지', '주차장', '', '', '해수욕장 개장 기간에는 반려견 동반이 제한될 수 있습니다. 반려견 인식표 및 리드 줄, 입마개 필수입니다. 반려견 배변 시 보호자가 배변봉투, 물티슈 등 지참 후 즉시 처리하여야 합니다.<br>*반려견 동반 운영 정책은 현지 사정에 따라 변동 될 수 있습니다.');
INSERT INTO Tour_Info (TOUR_TITLE, ADDR, TOUR_CATEGORY, FACILITIES, SUPPLIES, POLICY, NOTICE) VALUES ('순긋해변', '강원특별자치도 강릉시 해안로 682-5 (안현동)', '관광지', '야외 테이블', '', '대형견은 여름에만 동반 가능합니다.(야외테이블 이용 가능 계절, 보호자의 각별한 주의를 요청드립니다.)<br>고양이나 다른 반려동물도 입장 가능하나, 동물에 따라 필요한 경우 케이지를 이용해야 합니다.', '반려견 동반 시 야외 테이블 이용 부탁 드립니다. 겨울철 실내 이용 시 켄넬 필수 입니다. 짖음이 심하거나 예민한 반려견은 이용이 어렵습니다. 야외 좌석에서는 마킹하지 않도록 주의해주세요. 민감한 강아지는 켄넬 이용 등 조치 부탁드립니다. 여름에는 야외좌석을 이용, 겨울에는 켄넬 사용 시 실내도 입장 가능합니다.<br>*반려견 동반 운영 정책은 현지 사정에 따라 변동 될 수 있습니다.');
INSERT INTO Tour_Info (TOUR_TITLE, ADDR, TOUR_CATEGORY, FACILITIES, SUPPLIES, POLICY, NOTICE) VALUES ('시흥 갯골생태공원', '경기도 시흥시 동서로 287 (장곡동)', '관광지', '주차장', '', '안내견', '전시실 내에는 안내견 이외의 애완동물 출입은 금지되어 있습니다. 반려견 인식표 및 리드 줄, 입마개 필수입니다. 반려견 배변 시 보호자가 배변봉투, 물티슈 등 지참 후 즉시 처리하여야 합니다.<br>*반려견 동반 운영 정책은 현지 사정에 따라 변동 될 수 있습니다.<br>DMZ박물관은 통일안보공원을 지나 통일전망대로 가는 길목에 있으므로, 통일전망대 출입신고 및 교육을 받고 이용 가능합니다.');
INSERT INTO Tour_Info (TOUR_TITLE, ADDR, TOUR_CATEGORY, FACILITIES, SUPPLIES, POLICY, NOTICE) VALUES ('한반도지형 (강원고생대 국가지질공원)', '강원특별자치도 영월군 한반도면 한반도로 555 (한반도면)', '관광지', '주차장(주차요금 : 경차 1,000원, 소형 2,000원, 대형 3,000원)', '', '소형견 (대형견 불가)', '반려견 인식표 및 리드 줄, 입마개 필수입니다. 반려견 배변 시 보호자가 배변봉투, 물티슈 등 지참 후 즉시 처리하여야 합니다. 대형견은 동반 입장 불가입니다. 소형견은 안고 동반 가능합니다.<br>*반려견 동반 운영 정책은 현지 사정에 따라 변동 될 수 있습니다.');
